import React, { Component, Fragment } from "react";
import FriendComponent from "./FriendsComponent";
// import { connect } from 'react-redux';
// import { push } from 'connected-react-router';
import { string, number } from "prop-types";
// import ItemAdd from "../itemAdd";
import profile from './png/profile.png'

const propTypes = {
  name: string,
  logoImg: string,
  value: number,
  valueGrn: number,
};

class FriendContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      valueGrn: "",
      addData: [
        {name: 'Vlad Andreyevich', owe: 30, id: 1},
        {name: 'Vlad Andreyevich', owe: 30, id: 1},
        {name: 'Vlad Andreyevich', owe: 30, id: 1},
      ]
    };
    this.onClickHandler = this.onClickHandler.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  maxId = 100;

  onClickHandler = () => {
    console.log(this.state.value);
  };

  addItem = (text) => {
    const newItem = {
      name: this.state.value,
      owe: this.state.valueGrn,
      id: this.maxId++
    };

    this.setState(({ addData }) => {
      const newArr = [
        ...addData,
         newItem
      ];
      return {
        addData: newArr,
      };
    });
  };



  render() {
    const elements = this.state.addData.map((item) =>  {
      return (
        <Fragment key={item.id}>
          <img src={profile} className='profile-img' alt='prifileImg'/>
          <p className='name'>{item.name}</p>
          <div className='oves-wrapper'>
            <p className='oves'>oves you</p>
            <p className='summa'>{item.owe} USD</p>
          </div>
          <div className='arrow'></div>
        </Fragment>
      )
    })
    return <FriendComponent 
    onClickHandler={this.onClickHandler}
    addItem={this.addItem} 
    elements={elements}
    />;
  }
}

FriendContainer.propTypes = propTypes;

export default FriendContainer;
