import React, {Component, Fragment} from 'react';
import AddPageComponent from './AddPageComponent';
import FriendsContainer from  '../Friends/FriendsContainer'
import { push } from 'connected-react-router';
import { func } from 'prop-types';
import { connect } from 'react-redux';

const propTypes = {
	push: func,
  SaveClick: func,
  AddItem: func,
  CloseClick: func,
  handleChangeGrn: func,
  handleChangeList: func,
};

class AddPAgeContainer extends Component {

  maxId = 100;

    constructor(props) {
    super(props);
    this.state = {
      value: '',
      valueGrn: '',
      addData: [
        {list: 'something', grn: 0, id: 1}
      ]
  };

		this.AddItem = this.AddItem.bind(this);
		this.CloseClick = this.CloseClick.bind(this);
    this.SaveClick = this.SaveClick.bind(this);
    this.handleChangeGrn = this.handleChangeGrn.bind(this);
    this.handleChangeList = this.handleChangeList.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }


    CloseClick = () => {
      console.log('piska Close');
    };

    SaveClick = () => {
      console.log('piska Save');
    };

    handleChangeList = (event) => {
      this.setState({value: event.target.value})
    };

    handleChangeGrn = (event) => {
      this.setState({valueGrn: event.target.value})
    };

    handleSubmit = (event) => {
      console.log('було надіслано: '+ this.state.value );
      console.log('було надіслано: '+ this.state.valueGrn );
      event.preventDefault();
    };

    AddItem = (text) => {
      const newItem = {
        list: this.state.value,
        grn: this.state.valueGrn,
        id: this.maxId++,
      };

      this.setState(({ addData }) => {
        const newArr = [
          ...addData,
          newItem
        ]
        return {
          addData: newArr
        };
      });
    };
    render () {
        return (
          <Fragment>
            <AddPageComponent 
              onClickHandler={this.onClickHandler}
              SaveClick={this.SaveClick}
              CloseClick={this.CloseClick}
              handleChangeList={this.handleChangeList}
              handleChangeGrn={this.handleChangeGrn}
              handleSubmit={this.handleSubmit}
            />
            <FriendsContainer 
            value={this.state.value}
            valueGrn={this.state.valueGrn}/>
        </Fragment>
        ); 
    }
}

// const mapStateToProps = propTypes => {
//   return{

//   }
// };

const mapDispatchToProps = {
	push,
};

AddPageComponent.propTypes = propTypes;

export default connect(null, mapDispatchToProps)(AddPAgeContainer);
