import React, { Component } from "react";
import FriendComponent from "./FriendsComponent";
import AddPageContainer from "../AddPage/AddPageContainer";
// import { connect } from 'react-redux';
// import { push } from 'connected-react-router';
import { string, number } from "prop-types";

const propTypes = {
  name: string,
  logoImg: string,
  owe: number,
  lent: number
};

class FriendContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      valueGrn: "",
      addData: []
    };
    this.onClickHandler = this.onClickHandler.bind(this);
  }

  onClickHandler = () => {
    console.log(this.state.valueGrn);
  };

  AddItem = () => {
    const newItem = {
      list: this.state.value,
      grn: this.state.valueGrn,
      id: this.maxId++
    };

    this.setState(({ addData }) => {
      const newArr = [...addData, newItem];
      return {
        addData: newArr
      };
    });
  };

  render() {
    return <FriendComponent onClickHandler={this.onClickHandler} />;
  }
}

FriendContainer.propTypes = propTypes;

export default FriendContainer;
